function insertionSort(arr) {
  for (let index = 1; index < arr.length; index++) {
    let temp = arr[index];
    let aux = index - 1;

    while (aux >= 0 && arr[aux] > temp) {
      arr[aux + 1] = arr[aux];
      aux--;
    }
    arr[aux + 1] = temp;
  }
}

